import React, { useState, useEffect } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import CRUDHeader from './CRUDHeader';
import './CRUD.css';
import axios from 'axios';

function ReadJoy() {
  const { no } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // 백엔드에서 게시글 목록을 가져옴
    const fetchPost = async () => {
      try {
        // const response = await axios.get(`/joy/PostView/:no`);
        const response = await axios.get(`/joy/PostView/${no}`); // API 엔드포인트 문자열 보간
        console.log('응답 데이터:', response.data);
        setPost(response.data);
        setLoading(false);
      } catch (error) {
        console.error('게시글을 불러오는 중 오류 발생:', error);
        setError('게시글을 불러오는 중 오류 발생');
        setLoading(false);
      }
    };

    fetchPost();
  }, [no]);

  const handleDelete = async () => {
    const confirmDelete = window.confirm('정말로 삭제하시겠습니까?');

    if (confirmDelete) {
      try {
        await axios.delete(`/joy/Postview/${no}/process/delete`);
        alert('게시글이 삭제되었습니다.');
        navigate('/joy');
      } catch (error) {
        if (error.response && error.response.status === 403) {
          alert('삭제 권한이 없습니다.');
        } else {
          console.error('게시글 삭제 중 오류 발생:', error);
          alert('게시글 삭제 중 오류가 발생했습니다.');
        }
      }
    }
  };

  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!post) {
    return <div>게시글을 찾을 수 없습니다.</div>;
  }

  const { title, nickname, created_date, content } = post.post;

  return (
    <div className="post-container">
      <CRUDHeader title="기쁨이 게시판" />
      <div>{title}</div>
      <div className="infoUpdateDelete">
        <div className="info">
          <div>프사</div>
          <div>{nickname}</div>
          <div>{created_date}</div>
        </div>
        <div className="updateDelete">
          <Link to={`/joy/Postview/${no}/process/update`}>수정</Link>
          <div onClick={handleDelete} style={{ cursor: 'pointer' }}>
            삭제
          </div>
        </div>
      </div>
      <div>
        <p>HOPINFO는 서로의 아픔을 공감하고 위로하는 커뮤니티입니다.</p>
        <p>회원들끼리 서로 존중하고, 응원과 조언을 아끼지 않는 자랑스러운 회원이 되도록 합시다.</p>
      </div>
      <div>{content}</div>
      <div>
        <div>로고</div>
        <div>댓글</div>
      </div>
      <div>댓글들</div>
      <div>댓글입력창</div>
    </div>
  );
}

export default ReadJoy;
