import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Loginpage.css";
import axios from "axios";

function Loginpage() {
  const navigate = useNavigate();
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [kakaoId, setKakaoId] = useState(""); // 카카오 아이디 상태 추가
  const [kakaoPassword, setKakaoPassword] = useState(""); // 카카오 비밀번호 상태 추가

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("/loginpage/process/login", {
        id,
        password,
      });
      const data = response.data;

      if (data.success) {
        alert("로그인 성공!");
        navigate("/"); // 로그인 성공 시 메인페이지 이동
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("로그인 요청 중 오류 발생:", error);
      alert("로그인 요청 중 오류가 발생했습니다.");
    }
  };

  const handleKakaoLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("/auth/kakao/direct", {
        kakaoId,
        kakaoPassword,
      });
      const data = response.data;
      if (data.success) {
        alert("카카오 로그인 성공!");
        navigate("/");
      } else {
        alert("카카오 로그인 실패: " + data.message);
      }
    } catch (error) {
      console.error("카카오 로그인 요청 중 오류 발생:", error);
      alert("카카오 로그인 요청 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="loginPage">
      <h1>로그인</h1>
      <form className="loginPagecontent" onSubmit={handleSubmit}>
        <div className="textBox">
          <input
            type="text"
            name="id"
            placeholder="아이디"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
          <input
            type="password"
            name="password"
            placeholder="비밀번호"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="btnContent">
          <button type="submit" className="loginpageBtn">
            로그인
          </button>
          <button className="signuppageBtn">
            <Link to="/Signuppage" className="signupLink">
              회원가입
            </Link>
          </button>
        </div>
      </form>
      <h2>카카오톡 계정 로그인</h2>
      <form className="kakaoLoginForm" onSubmit={handleKakaoLogin}>
        <div className="textBox">
          <input
            type="text"
            name="kakaoId"
            placeholder="카카오 아이디"
            value={kakaoId}
            onChange={(e) => setKakaoId(e.target.value)}
          />
          <input
            type="password"
            name="kakaoPassword"
            placeholder="카카오 비밀번호"
            value={kakaoPassword}
            onChange={(e) => setKakaoPassword(e.target.value)}
          />
        </div>
        <div className="btnContent">
          <button type="submit" className="kakaoLoginBtn">
            카카오 계정으로 로그인
          </button>
        </div>
      </form>
    </div>
  );
}

export default Loginpage;
