import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import io from "socket.io-client";
import useUserData from "../useUserData";

const socket = io();

const Chat = () => {
  const navigate = useNavigate();
  const { usernickname, fetchUserData } = useUserData();
  const { roomid } = useParams(); // URL의 roomid 파라미터를 가져옴
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  useEffect(() => {
    fetchUserData();

    socket.on("receiveMessage", (message) => {
      setChat((prevChat) => [...prevChat, message]);
    });

    // Join room once user data is fetched
    if (roomid) {
      socket.emit("joinRoom", roomid);
      console.log(`Joined room: ${roomid}`);
    }

    return () => {
      socket.off("receiveMessage");
    };
  }, [roomid]);

  useEffect(() => {
    if (usernickname) {
      console.log(`User nickname: ${usernickname}`);
    }
  }, [usernickname]);

  const sendMessage = () => {
    if (roomid && usernickname) {
      socket.emit("sendMessage", {
        sender_id: usernickname,
        receiver_id: roomid,
        content: message,
      });
      setMessage(""); // Clear message input after sending
    } else {
      alert("Please join a room first and enter your User ID.");
    }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={usernickname}
          placeholder="Your User ID"
          disabled
        />
        <input type="text" value={roomid} placeholder="Room ID" disabled />
      </div>
      <div id="chat">
        {chat.map((msg, index) => (
          <div key={index}>
            {msg.sender_id}: {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Enter message"
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default Chat;
