import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import io from "socket.io-client";
import useUserData from "../useUserData";

const socket = io();

const Chat = () => {
  const navigate = useNavigate();
  const { usernickname, roomid, setUsernickname, setroomid, fetchUserData } =
    useUserData();
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  useEffect(() => {
    fetchUserData();

    socket.on("receiveMessage", (message) => {
      setChat((prevChat) => [...prevChat, message]);
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, []);

  useEffect(() => {
    if (setroomid) {
      console.log(`Room ID: ${setroomid}`);
      socket.emit("joinRoom", setroomid);
    }
    if (usernickname) {
      console.log(`User Nickname: ${setUsernickname}`);
    }
  }, [setroomid, setUsernickname]);

  const sendMessage = () => {
    if (setroomid && setUsernickname) {
      socket.emit("sendMessage", {
        sender_id: setUsernickname,
        receiver_id: setroomid,
        content: message,
      });
      setMessage(""); // Clear message input after sending
    } else {
      alert("Please join a room first and enter your User ID.");
    }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={setUsernickname}
          placeholder="Your User ID"
          disabled
        />
        <input type="text" value={setroomid} placeholder="Room ID" disabled />
      </div>
      <div id="chat">
        {chat.map((msg, index) => (
          <div key={index}>
            {msg.sender_id}: {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Enter message"
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default Chat;
